import React, { Component } from 'react';
import { Row, Col, Form, Button, Container } from 'react-bootstrap'
import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost:3001/api/v1/user_activities'
})

class Add extends Component {

    state = {
        name: '',
        comment: '',
        duration: '',
        date: ''
    }

    handleChange = (e, { name, value }) => this.setState({ [name]: value });

    handleSubmit = () => {
        const data = {
            name: this.state.name,
            description: this.state.comment,
            duration: this.state.duration,
            date: this.state.date
        };
        api.post('/', data)
        .then(res => console.log(res))
        .catch(err => console.log(err))
    };

    render() {

        const { name, comment, duration, date } = this.state

        return (
            <div>
                <h1>Add an activity</h1>
                <Container style={{ padding: '0 10vw' }}>
                    <Form>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridEmail">
                                <Form.Label>Date</Form.Label>
                                <Form.Control onChange={this.handleChange} placeholder="Select the date of your workout" />
                            </Form.Group>
                            <Form.Group as={Col} controlId="formGridEmail">
                                <Form.Label>Activity name</Form.Label>
                                <Form.Control onChange={this.handleChange} placeholder="Select activity" />
                            </Form.Group>
                            <Form.Group as={Col} controlId="formGridEmail">
                                <Form.Label>Duration</Form.Label>
                                <Form.Control onChange={this.handleChange} placeholder="Select a duration" />
                            </Form.Group>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col} controlId="formGridEmail">
                                <Form.Label>Comment</Form.Label>
                                <Form.Control as='textarea' onChange={this.handleChange} placeholder="How was your workout ?" />
                            </Form.Group>
                        </Form.Row>
                        <Button variant="primary" type="submit">
                            Submit
                        </Button>
                    </Form>
                </Container>
            </div>
        )
    }
}
  
export default Add;
