import React, { Component } from 'react';
import { Col, Form, Button, Container } from 'react-bootstrap'
import axios from 'axios'

const api = axios.create({
    baseURL: 'http://localhost:3001/api/v1/sessions'
})

class Singup extends Component {

    state = {
        email: '',
        password: '',
        // confirmed_password: '',
    }

    // handleChange = (e, { e.target.name, e.target.value }) => this.setState({ [name]: value });

    handleChange = (event) => this.setState({name: event.target.name, value: event.target.value});

    handleSubmit = (e) => {
        const data = {
            email: this.state.email,
            password: this.state.password,
            // confirmed_password: this.state.confirmed_password
        };
        /*api.post('/', data)
        .then(res => console.log(res))
        .catch(err => console.log(err))*/
        console.log(data)
        e.preventDefault();
    };

    render() {

        const { email, password } = this.state

        return (
            <Container style={{ padding: '0 10vw' }}>
                <Form>
                    <Form.Row>
                        <Form.Group as={Col} controlId="formGridEmail">
                            <Form.Label>Email</Form.Label>
                            <Form.Control value={ email } onChange={ (event) => { this.state.email = event.target.value } } type="email" placeholder="Enter email" />
                        </Form.Group>
                    </Form.Row>
                    <Form.Row>
                        <Form.Group as={Col} controlId="formGridPassword">
                            <Form.Label>Password</Form.Label>
                            <Form.Control value={ password } onChange={ (event) => { this.state.password = event.target.value } } type="password" placeholder="Password" />
                        </Form.Group>
                    </Form.Row>
                    {/* <Form.Row>
                        <Form.Group as={Col} controlId="formGridPassword">
                            <Form.Label>Confirm password</Form.Label>
                            <Form.Control value={ confirmed_password } onChange={this.handleChange} type="password" placeholder="Confirm password" />
                        </Form.Group>
                    </Form.Row> */}

                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Form>
            </Container>
        )
    }
}
  
export default Singup;
